import axios from 'axios';

class ProductsService {
  constructor() {
    this.getAllProducts = () => this.app.get('/product');

    this.getOneProduct = product_id => this.app.get(`/product/${product_id}`);

    this.deleteProduct = product_id => this.app.delete(`/product/delete/${product_id}`);

    this.editProduct = (product_id, productDetails) => this.app.put(`/product/edit/${product_id}`, productDetails);

    this.addFavorite = product_id => this.app.put('/product/favorite/add', product_id);

    this.removeFavorite = product_id => this.app.put(`/product/favorite/remove/${product_id}`);

    this.getFavorites = () => this.app.get('product/favorite/myfavorite');

    this.getProductPerCategory = category_id => this.app.get(`/product/category/${category_id}`);

    this.createProduct = (productDetails, id) => this.app.post(`/client/myarea/${id}`, productDetails);

    this.getProductBySupplier = supplier_id => this.app.get(`/client/myarea/myproducts/${supplier_id}`);

    this.getMyProductDetails = product_id => this.app.get(`/client/myarea/myproductdetails/${product_id}`);

    this.app = axios.create({
      baseURL: `${process.env.REACT_APP_BASE_URL}`,
      withCredentials: true
    });
  }

}

export default ProductsService;
